import React, { Component } from 'react';
import Table from './ui/index'
import api from './ui/api_Task/'
const apiTodo = api.todo


class App extends Component {
  constructor(props) {
    super(props)
    this.state = props.state
    this.axioTaskList = this.axioTaskList.bind(this)
    this.renderArrTask= this.renderArrTask.bind(this)
    this.dispath = props.dispath
  }
  axioTaskList() {
    apiTodo.getTasks({}, response => {      
        this.renderArrTask(response.data.results)   
  })

  }
  renderArrTask(data){
    let newstate = {...this.state};
    let opisTask = newstate.opisTask;
    data.map(newtask => {
      opisTask = opisTask.map(oldtask => {
        if(newtask.id === oldtask.id) {
          console.log(oldtask, 'oldtask')
          console.log(newtask, 'newtask')

          return {...newtask};
        }
        return oldtask;
      })
    });
    newstate.opisTask = opisTask;
    console.log(newstate.opisTask);
    debugger
    // this.setState(newstate.opisTask);
    this.dispath({
      type:'RERENDER_THREE',
      contextState:newstate.opisTask
    })
  }

  componentDidMount() {
    this.axioTaskList()
  }
  render() {

    return (
      <Table state={this.state} rerenderTask={this.renderArrTask} />
    )
  }
}

export default App;
