import React , {Component} from 'react'
import FlagWork from './FlagWork'
import GroupButton from './GroupButton'
import OpisTask from './OpisTask'
import Podrob from './Podrob'
import './styles/style.css'
class TrTable extends Component {
    constructor(props){
        super(props)


        this.state = props.state
        this.cklicked = this.cklicked.bind(this)
        this.trRenderTable = this.trRenderTable.bind(this)
    }
    cklicked(){
        if(this.state.opis){
        this.state.opis = false
        }else{
            this.state.opis = true   
        }
        this.setState(this.state)
    }
    trRenderTable(){
        this.state.opisTask.map((el,i) =>{

            return(
                <tr className='row_style'>
                <th scope="row">{el.i}</th>
                <td >
                    <FlagWork />
                </td>
                <td>
                    <GroupButton />
                </td>
                <td  onClick={this.cklicked}>
                    <OpisTask state={this.state }/>
                </td>
                 {this.state.opis ? <Podrob />: <td></td>}
            </tr>
            )
        })
    }
    render(){
        return(
            <>
                 { this.trRenderTable() }        
            </>
        )
    }
}
export default TrTable
