import React , {Component} from 'react'
import { Manager, Reference, Popper } from 'react-popper';
import './styles/style.css'
class GroupButton extends Component {
    constructor(props) {
        super(props);
        this.clickBurg = this.clickBurg.bind(this)
        this.state = props.state
    }
    clickBurg(){

        return this.setState(this.state.buttonCliked = !this.state.buttonCliked)

    }
    render(){
        return(
            <div>

                <button className='btn btn-primary'>Взять задачу</button>

                <div className="Burger_button" onClick={this.clickBurg}>

                    <span></span>
                    <span></span>
                    <span></span>
                 </div>
                 {this.state.buttonCliked ? (   <div className='clickedBurger'>
                        <button type="button" className='btn btn-danger'>Закрыть задачу</button>
                        <button type="button" className='btn btn-secondary'>Редактировать задачу</button>
                        <button type="button" className='btn btn-success'>Завершить задачу</button>
                        <button type="button" className='btn btn-info'>Переоткрыть задачу</button>

                   </div>  ) : <p></p>}

                <a href="#" className="link_task">перейти</a>
            </div>
        )
    }
}
export default GroupButton
