import React, { Component } from 'react';
import Table from './ui/index'
import api from './ui/api_Task/'
const apiTodo = api.todo


class App extends Component {
  constructor(props) {
    super(props)
    this.state = props.state
    this.axioTaskList = this.axioTaskList.bind(this)
    this.renderArrTask = this.renderArrTask.bind(this)
    this.dispath = props.dispath
  }
  axioTaskList() {
    apiTodo.getTasks({}, response => {
      let mass = []
      let data = response.data;
      // this.renderArrTask(response.data.results)  
      for (let i = 0; i < data.results.length; i++) {
        if (this.data.results[i].owner_object.id === this.state.owner_object.id) {
          mass.push(data.results[i])
        }
        // this.state.buttonCliked[this.data.results[i].id] = null

      }
    })

  }
  renderArrTask(data) {
    // let newstate = {...this.state};
    let opisTask = this.state.opisTask;
    data.map(newtask => {
      opisTask = opisTask.map(oldtask => {
        if (newtask.id === oldtask.id) {
          console.log(oldtask, 'oldtask')
          console.log(newtask, 'newtask')

          return { ...newtask };
        }
        return oldtask;
      })
    });
    this.state.opisTask = opisTask;
    console.log(this.state.opisTask);
    debugger
    // this.setState(this.state.opisTask);
    this.dispath({
      type: 'RERENDER_THREE',
      contextState: this.state.opisTask
    })
  }

  componentDidMount() {
    this.axioTaskList()
  }
  render() {

    return (
      <Table state={this.state} rerenderTask={this.renderArrTask} />
    )
  }
}

export default App;
