import * as axios  from 'axios';
// import state from '../state/state'
const urlBase = 'http://garpixcms.staging.garpix.com/garpix_workflow/api/v1/';


export default class axioPost{
    constructor(el , urlProps , state) {

        this.el = el
        this.urlProps = urlProps
    }
    axioRequests(){

        const headers = {
            'Authorization': 'Token 6668b8cfdf927ba7710a9b0890de66c12d0b69fa'
        }
        axios.post(`${urlBase}task/${this.el}${this.urlProps}`, {}, {headers})
          .then((response) => {
            console.log(response.data)
             state.opisTask.map((el,i) =>{

                if(state.opisTask[i].id === response.data[0].id){
                  state.opisTask[i] = response.data[0]
                }else if(state.opisTask[i].id === response.data[1].id){
                  state.opisTask[i] = response.data[1]
                }
                console.log( state);
                this.setState(state)
             })

          })
          .catch((error) => {
            console.log(error);
          });
    }

}
