import React , {Component} from 'react'
import axioPost from './axioButton'
import './styles/style.css'




class GroupButton extends Component {
    constructor(props) {
        super(props);
        this.clickBurg = this.clickBurg.bind(this)
        this.funClickClose = this.funClickClose.bind(this)
        // this.funClickStartTask = this.funClickStartTask.bind(this)
        this.state = props.state
        this.el = props.el
    }
    clickBurg(){
       if(this.state.buttonCliked){        
        this.state.buttonCliked = false          
       }else{
        this.state.buttonCliked = true       
       }
        return this.setState(this.state)

    }
   funClickClose(){
    let urlProps = '/close/'
    let close = new axioPost(this.el.id , urlProps).axioRequests()

   }
//    funClickStartTask(){
//     const headers = {
//         'Authorization': 'Token 6668b8cfdf927ba7710a9b0890de66c12d0b69fa'
//     }
//     axios.post(`${urlBase}task/${this.el.id}/start_develop/`, {}, {headers})
//       .then((response) => {
//         console.log(response);
//       })
//       .catch((error) => {
//         console.log(error);
//       });
//    }
    render(){
        return(
            <div>

                <button className='btn btn-primary'>Взять задачу</button>

                <div className="Burger_button" onClick={this.clickBurg}>

                    <span></span>
                    <span></span>
                    <span></span>
                 </div>
                 {this.state.buttonCliked ? (   <div className='clickedBurger'>
                        <button type="button" className='btn btn-danger' onClick={this.funClickClose}>Закрыть задачу</button>
                        <button type="button" className='btn btn-secondary' onClick={this.funClickStartTask}>Редактировать задачу</button>
                        <button type="button" className='btn btn-success'>Завершить задачу</button>
                        <button type="button" className='btn btn-dark'>Переоткрыть задачу</button>

                   </div>  ) : <p></p>}

                <a href="#" className="link_task">перейти</a>
            </div>
        )
    }
}
export default GroupButton
